<script>

export default {
	name: '15YearsPopcorn',
	mounted() {
		const getRandomInt = (minimum, maximum) => {
			const min = Math.ceil(minimum);
			const max = Math.floor(maximum);
			return Math.floor(Math.random() * (max - min + 1)) + min;
		};
		const nums = Array.from({ length: 25 }, () => getRandomInt(0, 25));
		nums.forEach(num => {
			const speed = getRandomInt(10, 50);
			const direction = getRandomInt(0, 1) === 0 ? -1 : 1;
			this.addRotateTransform(`popcornPath${num}`, speed, direction);
		});
	},
	methods: {
		addRotateTransform(targetId, dur, dir) { // http://www.petercollingridge.co.uk/tutorials/svg/animation/rotation/
			const myElement = this.$refs[targetId];
			const a = document.createElementNS('http://www.w3.org/2000/svg', 'animateTransform');
			const bb = myElement.getBBox();
			const cx = bb.x + bb.width / 2;
			const cy = bb.y + bb.height / 2;
			a.setAttributeNS(null, 'attributeName', 'transform');
			a.setAttributeNS(null, 'attributeType', 'XML');
			a.setAttributeNS(null, 'type', 'rotate');
			a.setAttributeNS(null, 'dur', `${dur}s`);
			a.setAttributeNS(null, 'repeatCount', 'indefinite');
			a.setAttributeNS(null, 'from', `0 ${cx} ${cy}`);
			a.setAttributeNS(null, 'to', `${360 * dir} ${cx} ${cy}`);
			if (a.beginElement) {
				myElement.appendChild(a);
				a.beginElement();
			}
		}
	}
};
</script>

